mapper.select:
SELECT tt.cn_note_id, tt.cn_note_title, date_format(tt.cn_note_last_modify_time,'%Y-%c-%d') as cn_note_last_modify_time, tt.cn_note_times, tt.cn_note_body, tt.cn_note_tag, tt.cn_notebook_name, tt.cn_notebook_code, tt.cn_user_name FROM cn_share t LEFT JOIN ( SELECT t1.cn_note_id, t1.cn_note_title, t1.cn_note_last_modify_time, t1.cn_note_times, t1.cn_note_body, t1.cn_note_tag, t2.cn_notebook_name, t2.cn_notebook_code, t3.cn_user_name FROM cn_note t1 LEFT JOIN cn_notebook t2 ON t1.cn_notebook_code = t2.cn_notebook_code LEFT JOIN cn_user t3 ON t1.cn_user_id = t3.cn_user_id ) tt ON t.cn_note_id = tt.cn_note_id where 1=1
and tt.cn_notebook_code = #{noteType}
and tt.cn_note_body like CONCAT('%','${searchContent}','%')
and tt.cn_note_title like CONCAT('%','${searchContent}','%')
and tt.cn_note_tag like CONCAT('%','${searchContent}','%')
order by tt.cn_note_last_modify_time DESC limit #{begin},#{pageSize}

SELECT tt.cn_note_id, tt.cn_note_title, date_format(tt.cn_note_last_modify_time,'%Y-%c-%d') as cn_note_last_modify_time, tt.cn_note_times, tt.cn_note_body, tt.cn_note_tag, tt.cn_notebook_name, tt.cn_notebook_code, tt.cn_user_name FROM cn_share t LEFT JOIN ( SELECT t1.cn_note_id, t1.cn_note_title, t1.cn_note_last_modify_time, t1.cn_note_times, t1.cn_note_body, t1.cn_note_tag, t2.cn_notebook_name, t2.cn_notebook_code, t3.cn_user_name FROM cn_note t1 LEFT JOIN cn_notebook t2 ON t1.cn_notebook_code = t2.cn_notebook_code LEFT JOIN cn_user t3 ON t1.cn_user_id = t3.cn_user_id ) tt ON t.cn_note_id = tt.cn_note_id where 1=1
and tt.cn_notebook_code = #{noteType}
and tt.cn_note_body like CONCAT('%','${searchContent}','%')
and tt.cn_note_title like CONCAT('%','${searchContent}','%')
and tt.cn_note_tag like CONCAT('%','${searchContent}','%')
order by tt.cn_note_times DESC limit #{begin},#{pageSize}
