mapper.select:
SELECT t.cn_note_id, t.cn_note_title, t.cn_user_id, t2.cn_user_name, date_format(t.cn_note_last_modify_time,'%Y-%c-%d') AS cn_note_last_modify_time, t.cn_note_times, t.cn_note_body, t.cn_note_tag, t1.cn_notebook_name, t.cn_notebook_code FROM cn_note t LEFT JOIN cn_notebook t1 ON t.cn_notebook_code = t1.cn_notebook_code LEFT JOIN cn_user t2 ON t.cn_user_id = t2.cn_user_id where t.cn_user_id=#{userId}
and t.cn_notebook_code = #{noteType}
and t.cn_note_body like CONCAT('%','${searchContent}','%')
and t.cn_note_title like CONCAT('%','${searchContent}','%')
and t.cn_note_tag like CONCAT('%','${searchContent}','%')
ORDER BY t.cn_note_last_modify_time DESC limit #{begin},#{pageSize}

SELECT tt.cn_note_id, tt.cn_note_title, date_format(tt.cn_note_last_modify_time,'%Y-%c-%d') as cn_note_last_modify_time, tt.cn_note_times, tt.cn_note_body, tt.cn_note_tag, tt.cn_notebook_name, tt.cn_notebook_code, tt.cn_user_name, tt.cn_user_id FROM cn_share t LEFT JOIN ( SELECT t1.cn_note_id, t1.cn_note_title, t1.cn_note_last_modify_time, t1.cn_note_times, t1.cn_note_body, t1.cn_note_tag, t2.cn_notebook_name, t2.cn_notebook_code, t3.cn_user_name, t3.cn_user_id FROM cn_note t1 LEFT JOIN cn_notebook t2 ON t1.cn_notebook_code = t2.cn_notebook_code LEFT JOIN cn_user t3 ON t1.cn_user_id = t3.cn_user_id ) tt ON t.cn_note_id = tt.cn_note_id where 1=1 and tt.cn_user_id=#{userId}
and tt.cn_notebook_code = #{noteType}
and tt.cn_note_body like CONCAT('%','${searchContent}','%')
and tt.cn_note_title like CONCAT('%','${searchContent}','%')
and tt.cn_note_tag like CONCAT('%','${searchContent}','%')
order by tt.cn_note_times DESC limit #{begin},#{pageSize}

SELECT tt.cn_note_id, tt.cn_note_title, tt.cn_note_times, tt.cn_note_body, tt.cn_note_tag, tt.cn_notebook_name, tt.cn_notebook_code, tt.cn_user_name, tt.cn_user_id, date_format(t.cn_collect_date,'%Y-%c-%d') as cn_note_last_modify_time FROM cn_collect t LEFT JOIN ( SELECT t1.cn_note_id, t1.cn_note_title, t1.cn_note_last_modify_time, t1.cn_note_times, t1.cn_note_body, t1.cn_note_tag, t2.cn_notebook_name, t2.cn_notebook_code, t3.cn_user_name, t3.cn_user_id FROM cn_note t1 LEFT JOIN cn_notebook t2 ON t1.cn_notebook_code = t2.cn_notebook_code LEFT JOIN cn_user t3 ON t1.cn_user_id = t3.cn_user_id ) tt ON t.cn_note_id = tt.cn_note_id where 1=1 and t.cn_user_id=#{userId}
and tt.cn_notebook_code = #{noteType}
and tt.cn_note_body like CONCAT('%','${searchContent}','%')
and tt.cn_note_title like CONCAT('%','${searchContent}','%')
and tt.cn_note_tag like CONCAT('%','${searchContent}','%')
order by tt.cn_note_times DESC limit #{begin},#{pageSize}
